// Get transcribed text from Whisper
const inputText = $json.text || "";

// Replace spoken words with symbols
let cleanedText = inputText
  .toLowerCase()
  .replace(/at the rate/gi, "@")
  .replace(/dot/gi, ".")
  .replace(/underscore/gi, "_")
  .replace(/dash/gi, "-")
  .replace(/comma/gi, ",")
  .replace(/plus/gi, "+");

// Remove all spaces around email symbols and in the whole string
cleanedText = cleanedText.replace(/\s*@\s*/g, "@")
                         .replace(/\s*\.\s*/g, ".")
                         .replace(/\s*-\s*/g, "-")
                         .replace(/\s*,\s*/g, ",")
                         .replace(/\s*_\s*/g, "_")
                         .replace(/\s*\+\s*/g, "+")
                         .replace(/\s+/g, " ") // normalize multiple spaces
                         .trim();

return [
  {
    json: {
      text: cleanedText,
      original_text: inputText
    }
  }
];

const items = $input.all();
const fullText = items[0].json.output?.trim() || "";

let messagePart = "";
let jsonText = "";
let parsedJSON = {};

// Check for JSON with #JSON: prefix
const jsonFromHash = fullText.match(/#JSON:\s*({[\s\S]*})/);

// Check for JSON with or without "JSON:" or "---"
const jsonFromAny = fullText.match(/({[\s\S]*})/);

if (jsonFromHash) {
  // ‚úÖ Case 1: Found #JSON: { ... }
  jsonText = jsonFromHash[1].trim();
  messagePart = fullText.replace(/#JSON:\s*{[\s\S]*}/, "").trim();
} else if (jsonFromAny) {
  // ‚úÖ Case 2: Found JSON block, possibly after "JSON:" or "---"
  jsonText = jsonFromAny[1].trim();

  // Remove "JSON:", "---", or just the JSON block
  messagePart = fullText
    .replace(/---/, "")
    .replace(/JSON:/i, "")
    .replace(jsonText, "")
    .trim();
} else {
  // ‚ùå No JSON found
  jsonText = "{}";
  messagePart = fullText;
}

// Try parsing and converting keys to lowercase
try {
  const originalParsed = JSON.parse(jsonText);
  parsedJSON = {};

  for (const key in originalParsed) {
    parsedJSON[key.toLowerCase()] = originalParsed[key];
  }

} catch (e) {
  parsedJSON = { error: "Invalid JSON extracted", raw: jsonText };
}

// ‚úÖ Final output
return [
  {
    json: {
      message_output: messagePart,
      json_output: parsedJSON
    }
  }
];

