const rows = $input.all(); // All rows from Google Sheet

// Try to get meeting date from context (json_output from Switch1 or previous)
const context = $input.first().json || {};
const meetingDate = context['meeting_date'] || context['Meeting date'] || "undefined";

// Filter meetings for selected date and not cancelled
const meetings = rows.filter(item => 
  item.json["Meeting date"] === meetingDate &&
  item.json["Status"] !== "cancelled"
);

let message_output;

if (meetings.length === 0) {
  message_output = `No confirmed meetings found.`;
} else {
  message_output = `Here are your confirmed meetings for ${meetingDate}:\n\n`;

  for (const meeting of meetings) {
    const start = meeting.json["Start Time (IST)"] || meeting.json["Start Time"] || "unknown";
    const end = meeting.json["End Time (IST)"] || meeting.json["End Time"] || "unknown";
    const title = meeting.json["Discussion topic"] || meeting.json["Title"] || "No topic";

    message_output += `üïí ${start} - ${end}\nüìå ${title}\n\n`;
  }
}

return [
  {
    json: {
      message_output,
      WA_ID: $('WhatsApp Trigger').first().json.contacts[0].wa_id
    }
  }
];

const start = new Date($('Code5').first().json.json_output.start_date);
const end = new Date($('Code5').first().json.json_output.end_date);

const result = items.filter(item => {
  const rowDate = new Date(item.json['Meeting date']);
  const status = item.json.Status?.toLowerCase();
  return (
    rowDate >= start &&
    rowDate <= end &&
    status !== "cancelled"
  );
});

return result;

const meetings = $input.all(); // Filtered rows from previous node

let message_output;

if (meetings.length === 0) {
  message_output = `No confirmed meetings found in the next 7 days.`;
} else {
  // Sort by Meeting date (ascending)
  meetings.sort((a, b) => {
    const dateA = new Date(a.json["Meeting date"]);
    const dateB = new Date(b.json["Meeting date"]);
    return dateA - dateB;
  });

  message_output = `üìÖ *Your upcoming confirmed meetings (next 7 days):*\n\n`;

  for (const meeting of meetings) {
    const date = meeting.json["Meeting date"] || "Unknown date";
    const start = meeting.json["Start Time (IST)"] || meeting.json["Start Time"] || "unknown";
    const end = meeting.json["End Time (IST)"] || meeting.json["End Time"] || "unknown";
    const title = meeting.json["Discussion topic"] || meeting.json["Title"] || "No topic";

    message_output += `üìÜ *${date}*\nüïí ${start} - ${end}\nüìå ${title}\n\n`;
  }
}

// Return message and WhatsApp ID
return [
  {
    json: {
      message_output,
      WA_ID: $('WhatsApp Trigger').first().json.contacts[0].wa_id
    }
  }
];
